import React, { Fragment } from "react";
import "../Css/Gallery.css"
import $ from 'jquery'

function getDataFromLS() {
  try {
    let img = localStorage.img;
    if (img) {
      img = JSON.parse(img);
    }
    if (Array.isArray(img) && img.length > 0) {
      return img;
    }
  } catch (error) {
    console.log("error : ", error);
  }
  return [];
}

function encodeImageFileAsURL(event) {
  event.preventDefault();
  alert("You upload the image");
  let file = event.target[0].files;
  const img = getDataFromLS();
  Object.keys(file).forEach((x, i) => {
    let reader = new FileReader();
    reader.onloadend = function () {
      img.push(reader.result);
      if (i + 1 === Object.keys(file).length) {
        localStorage.img = JSON.stringify(img);
        console.log(localStorage.img);
      }
    };
    reader.readAsDataURL(file[x]);
    console.log(reader.readAsDataURL(file[x]));
  });
}
const img = getDataFromLS();
if (img && img.length > 0) {
  const getImg = document.getElementById("getImg");
  img.forEach((element, index) => {
    const newImg = document.createElement("img");
    newImg.src = element;
    newImg.id = index;
    getImg.appendChild(newImg);
  });
} 
$(document).ready(function () {
  $("img").dblclick(function (e) {
    const id = e.target.id;
    alert("You Added a Image in your favorite images");
    const img = getDataFromLS("img");
    const favImg = getDataFromLS("favimg");
    favImg.push(img[+id]);
    localStorage.favimg = JSON.stringify(favImg);
  });
});
const favimg = getDataFromLS("favimg");
if (favimg && favimg.length > 0) {
  const getImg = document.getElementById("favImg");
  favimg.forEach((element, index) => {
    const newImg = document.createElement("img");
    newImg.src = element;
    newImg.id = index;
    getImg.appendChild(newImg);
  });
} 
export default function GalleryPage(props) {
  return (
    <Fragment>
      <div className="galHeader">
        <a href="uploadImg.html">
          <button className="button">Back</button>
        </a>
        <h3>Your memories are store here</h3>
        <a href="favorite.html">
          <button className="button">Next</button>
        </a>
      </div>
      <div id="getImg"></div>
    </Fragment>
  );
}
