import React from 'react';
import "../Css/Upload.css";
import image from "../Assets/logo.png";

function getDataFromLS() {
  try {
    let img = localStorage.img;
    if (img) {
      img = JSON.parse(img);
    }
    if (Array.isArray(img) && img.length > 0) {
      return img;
    }
  } catch (error) {
    console.log("error : ", error);
  }
  return [];
}

function encodeImageFileAsURL(event) {
  event.preventDefault();
  alert("You upload the image");
  let file = event.target[0].files;
  const img = getDataFromLS();
  Object.keys(file).forEach((x, i) => {
    let reader = new FileReader();
    reader.onloadend = function () {
      img.push(reader.result);
      if (i + 1 === Object.keys(file).length) {
        localStorage.img = JSON.stringify(img);
        console.log(localStorage.img);
      }
    };
    reader.readAsDataURL(file[x]);
    console.log(reader.readAsDataURL(file[x]));
  });
}
const img = getDataFromLS();
if (img && img.length > 0) {
  const getImg = document.getElementById("getImg");
  img.forEach((element, index) => {
    const newImg = document.createElement("img");
    newImg.src = element;
    newImg.id = index;
    getImg.appendChild(newImg);
  });
} else {
  document.write("<h1>At this Momment there is no img Uploaded</h1>");
}
export default function UploadPage() {
  return (
    <>
      <div className="header">
        <img src={image} alt="" />
        <h3>Upload your memories and save for lifetime</h3>
        <a href="gallery.html">
          <button className="button">GalleryPage</button>
        </a>
      </div>
      <form onsubmit="encodeImageFileAsURL(event)" className="maindiv">
        <div className="file-input">
          <input type="file" multiple accept="image/*" required />
          <span className="button">Choose</span>
          <label className="label">Click here to select an image</label>
        </div>
        <div className="secondDiv">
          <h2 className="bottom-line">You want to upload the image</h2>
          <input type="submit" value="Upload" className="button-1" />
        </div>
      </form>
    </>
  );
}
