import React, { Fragment } from 'react';
import { useNavigate } from 'react-router-dom';
import '../Css/Gallery.css';
import $ from 'jquery';

export default function GalleryPage() {
  React.useEffect(() => {
    function getDataFromLS() {
      try {
        let img = localStorage.img;
        if (img) {
          img = JSON.parse(img);
          console.log('img :', img);
        }
        if (Array.isArray(img) && img.length > 0) {
          return img;
        }
      } catch (error) {
        console.log('error : ', error);
      }
      return [];
    }

    const img = getDataFromLS();
    console.log('img :', img);
    if (img && img.length > 0) {
      const getImg = document.getElementById('getImg');
      img.forEach((element, index) => {
      // eslint-disable-next-line react/void-dom-elements-no-children
        const newImg = document.createElement('img');
        newImg.src = element;
        newImg.id = index;
        getImg.appendChild(newImg);
      });
    }
  }, []);

  const handleDoubleClick = () => {
    $(document).ready(() => {
      $('img').dblclick((e) => {
        const id = e.target.id;
        alert('You Added a Image in your favorite images');
        const img = getDataFromLS('img');
        const favImg = getDataFromLS('favimg');
        favImg.push(img[+id]);
        localStorage.favimg = JSON.stringify(favImg);
      });
    });
  }
  const navigate = useNavigate();
  function uploadPage() {
    navigate('/');
  }
  function favoritePage() {
    navigate('/favorite');
  }
  return (
    <>
      <div className="galHeader">
        <button className="button-4" onClick={uploadPage}>Back</button>
        <h3>Your memories are store here</h3>
        <button className="button-4" onClick={favoritePage}>Next</button>
      </div>
      <div id="getImg" />
    </>
  );
}
