import React, { Fragment } from 'react';
import $ from 'jquery';
import { useNavigate } from 'react-router-dom';
import '../Css/Favorite.css';

export default function Favorite() {
  function getDataFromLS(type) {
    console.log(type);
    try {
      let img = localStorage[type];
      if (img) {
        img = JSON.parse(img);
      }
      if (Array.isArray(img) && img.length > 0) {
        return img;
      }
    } catch (error) {
      console.log('error : ', error);
    }
    return [];
  }
  $(document).ready(() => {
    $('img').dblclick((e) => {
      const id = e.target.id;
      alert('You Added a Image in your favorite images');
      const img = getDataFromLS('img');
      const favImg = getDataFromLS('favimg');
      favImg.push(img[+id]);
      localStorage.favimg = JSON.stringify(favImg);
    });
  });
  const favimg = getDataFromLS('favimg');
  if (favimg && favimg.length > 0) {
    const getImg = document.getElementById('favImg');
    favimg.forEach((element, index) => {
      const newImg = document.createElement('img');
      newImg.src = element;
      newImg.id = index;
      getImg.appendChild(newImg);
    });
  }
  const navigate = useNavigate();
  function galleryPage() {
    navigate('/gallery');
  }
  return (
    <>
      <div className="favHeader">
        <h3>Your Favorite Images</h3>
        <button className="button" onClick={galleryPage}>Back</button>
      </div>
      <div id="favImg" className="col-sm" />
    </>
  );
}
