import React, { Fragment } from 'react';
import { useNavigate } from 'react-router-dom';
import '../Css/Gallery.css';

export default function GalleryPage() {

function getDataFromLS() {
  try {
    let img = localStorage.img;
    if (img) {
      img = JSON.parse(img);
    }
    if (Array.isArray(img) && img.length > 0) {
      return img;
    }
  } catch (error) {
    console.log("error : ", error);
  }
  return [];
}

function encodeImageFileAsURL(event) {
  event.preventDefault();
  alert("You upload the image");
  let file = event.target[0].files;
  const img = getDataFromLS();
  Object.keys(file).forEach((x, i) => {
    let reader = new FileReader();
    reader.onloadend = function () {
      img.push(reader.result);
      if (i + 1 === Object.keys(file).length) {
        localStorage.img = JSON.stringify(img);
        console.log(localStorage.img);
      }
    };
    reader.readAsDataURL(file[x]);
    console.log(reader.readAsDataURL(file[x]));
  });
}
const img = getDataFromLS();
if (img && img.length > 0) {
  const getImg = document.getElementById("getImg");
  img.forEach((element, index) => {
    const newImg = document.createElement("img");
    newImg.src = element;
    newImg.id = index;
    getImg.appendChild(newImg);
  });
} else {
  document.write("<h1>At this Momment there is no img Uploaded</h1>");
}
  const navigate = useNavigate();
  function uploadPage() {
    navigate('/');
  }
  function favoritePage() {
    navigate('/favorite');
  }
  return (
    <>
      <div className="galHeader">
        <button className="button-4" onClick={uploadPage}>Back</button>
        <h3>Your memories are store here</h3>
        <button className="button-4" onClick={favoritePage}>Next</button>
      </div>
      <div id="getImg" />
    </>
  );
}
